import React, { useEffect, useState } from 'react';
import HeaderBack from './Recommend/HeaderBack';
import Footer from './Home/Footer';

const Output = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [expandedInstructionsIndex, setExpandedInstructionsIndex] = useState(null);

  useEffect(() => {
    // Retrieve the recommendations from sessionStorage
    const storedRecommendations = JSON.parse(sessionStorage.getItem('recommendations')) || [];
    setRecommendations(storedRecommendations);
  }, []);

  useEffect(() => {
    // Fetch details for each recommendation
    const fetchAndDisplayFoodDetails = async (foodName) => {
      try {
        const response = await fetch(`http://localhost:8000/fetch-food-details/?food=${encodeURIComponent(foodName)}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const details = await response.json();
        console.log(details); // Log the fetched details

        // Update the recommendations state with the fetched details
        setRecommendations(prevRecommendations =>
          prevRecommendations.map(rec => (rec.food === foodName ? { ...rec, ...details } : rec))
        );
      } catch (error) {
        console.error('Error fetching food details:', error);
      }
    };

    recommendations.forEach(recommendation => {
      fetchAndDisplayFoodDetails(recommendation.food);
    });
  }, [recommendations]);

  const toggleInstructions = (index) => {
    setExpandedInstructionsIndex(index === expandedInstructionsIndex ? null : index);
  };

  const truncateInstructions = (instructions) => {
    const maxLength = 100; // Maximum length of instructions to display
    return instructions.length > maxLength ? `${instructions.substring(0, maxLength)}...` : instructions;
  };

  return (
    <div>
      <HeaderBack />
      <div>
        <div className="hero__title" style={{ paddingTop: '160px' }}>
          <h2>You may like these 🤗</h2>
        </div>
        <div className="container">
          <div id="recommendations" className="projcard-container">
            {recommendations.map((recommendation, index) => (
              <div key={index} className="projcard projcard-blue">
                <div className="projcard-innerbox">
                  <img className="projcard-img" src={recommendation.image} alt={recommendation.food} />
                  <div className="projcard-textbox">
                    <div className="projcard-title">{recommendation.food}</div>
                    <div className="projcard-subtitle">
                      Source:{' '}
                      <a href={recommendation.sourceUrl} target="_blank" rel="noreferrer">
                        {recommendation.sourceUrl}
                      </a>
                    </div>
                    <div className="projcard-bar"></div>
                    <div className="projcard-description">
                      Instructions:{' '}
                      {expandedInstructionsIndex === index ? (
                        <span>{recommendation.instructions}</span>
                      ) : (
                        <span>
                          {truncateInstructions(recommendation.instructions)}
                          <a href="#" onClick={() => toggleInstructions(index)} className="read-more">
                            {expandedInstructionsIndex === index ? 'Read less' : 'Read more'}
                          </a>
                        </span>
                      )}
                    </div>
                    <div className="projcard-tagbox">
                      <span className="projcard-tag">
                        {recommendation.ingredients
                          ? recommendation.ingredients.join(', ')
                          : 'Ingredients not available'}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Output;
