import React, { useState } from 'react';
import gluten from '../img/Recommend/gluten.svgz';
import dairy from '../img/Recommend/dairy.svgz';
import none from '../img/Recommend/none.svgz';

const AllergyCards = ({ onAllergyChange }) => {
 const [selectedAllergies, setSelectedAllergies] = useState({
    Gluten: false,
    Dairy: false,
    None: false,
 });

 const handleAllergyClick = (allergy) => {
    console.log(`Card selected: ${allergy}`); // Log the selected allergy to the console
    setSelectedAllergies(prevState => ({
      ...prevState,
      [allergy]: !prevState[allergy], // Toggle the selection state
    }));
    onAllergyChange(allergy);
 };

 return (
    <div>
      <div className="hero__title" style={{ paddingTop: '160px' }}>
        <h2>Do you have any allergies?</h2>
      </div>
      <div className="cards-list">
        <div className={`card ${selectedAllergies.Gluten ? 'active' : ''}`} onClick={() => handleAllergyClick('Gluten')}>
          <div className="card_image">
            <img src={gluten} alt="Gluten" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>Gluten</p>
            </div>
          </div>
        </div>
        <div className={`card ${selectedAllergies.Dairy ? 'active' : ''}`} onClick={() => handleAllergyClick('Dairy')}>
          <div className="card_image">
            <img src={dairy} alt="Dairy" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>Dairy</p>
            </div>
          </div>
        </div>
        <div className={`card ${selectedAllergies.None ? 'active' : ''}`} onClick={() => handleAllergyClick('None')}>
          <div className="card_image">
            <img src={none} alt="None" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>None</p>
            </div>
          </div>
        </div>
      </div>
    </div>
 );
};

export default AllergyCards;
