import React, { useEffect, useState } from 'react';
import Header from './Home/Header';


function Output() {
 const [recommendations, setRecommendations] = useState([]);

 useEffect(() => {
    const storedRecommendations = JSON.parse(sessionStorage.getItem('recommendations'));
    if (storedRecommendations) {
      setRecommendations(storedRecommendations);
      // Fetch and display details for each recommendation
      storedRecommendations.forEach(recommendation => {
        fetchAndDisplayFoodDetails(recommendation.food);
      });
    }
 }, []);

 const fetchAndDisplayFoodDetails = async (foodName) => {
    try {
       const response = await fetch(`/fetch-food-details/?food=${encodeURIComponent(foodName)}`);
       const details = await response.json();
       // Update the state with the fetched details
       // You might need to adjust this part based on how you want to display the details
       // For example, you could append the details to the recommendations state
       // or use a separate state to store the details and render them
    } catch (error) {
       console.error('Error fetching food details:', error);
    }
 };

 return (
    <div>
