import React, { useState } from 'react';
import gluten from '../img/Recommend/gluten.svgz';
import dairy from '../img/Recommend/dairy.svgz';
import none from '../img/Recommend/none.svgz';

const AllergyCards = ({ onAllergyChange }) => {
  const [selectedAllergies, setSelectedAllergies] = useState([]);

  const handleAllergyClick = (allergy) => {
    const updatedSelection = [...selectedAllergies];

    if (updatedSelection.includes(allergy)) {
      // If the allergy is already selected, remove it
      updatedSelection.splice(updatedSelection.indexOf(allergy), 1);
    } else {
      // Otherwise, add it to the selected allergies
      updatedSelection.push(allergy);
    }

    setSelectedAllergies(updatedSelection);
    onAllergyChange(updatedSelection); // Pass the updated selection to the parent component
  };

  const isAllergySelected = (allergy) => selectedAllergies.includes(allergy);

  return (
    <div>
      <div className="hero__title" style={{ paddingTop: '160px' }}>
        <h2>Do you have any allergies?</h2>
      </div>
      <div className="cards-list">
        <div
          className={`card ${isAllergySelected('Gluten') ? 'active' : ''}`}
          onClick={() => handleAllergyClick('Gluten')}
        >
          <div className="card_image">
            <img src={gluten} alt="Gluten" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>gluten</p>
            </div>
          </div>
        </div>
        <div
          className={`card ${isAllergySelected('Dairy') ? 'active' : ''}`}
          onClick={() => handleAllergyClick('Dairy')}
        >
          <div className="card_image">
            <img src={dairy} alt="Dairy" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>Dairy</p>
            </div>
          </div>
        </div>
        <div
          className={`card ${isAllergySelected('None') ? 'active' : ''}`}
          onClick={() => handleAllergyClick('None')}
        >
          <div className="card_image">
            <img src={none} alt="None" />
          </div>
          <div className="card_title title-white">
            <div style={{ paddingTop: '90px' }}>
              <p>None</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AllergyCards;
